        --model-location esm1v_t33_650M_UR90S_1 esm1v_t33_650M_UR90S_2 esm1v_t33_650M_UR90S_3 esm1v_t33_650M_UR90S_4 esm1v_t33_650M_UR90S_5 \
        --sequence "$1" \
        --dms-input "$2" \
        --mutation-col mutant \
        --dms-output "$3" \
        --scoring-strategy 'masked-marginals' \
        --offset-idx "$4"
}

